+//! Forwarder for one RPC method.
+class ChainImpl::RpcForwarder
+{
+public:
+    RpcForwarder(const CRPCCommand& command) : m_command(command)
+    {
+        m_command.actor = [this](const JSONRPCRequest& request) { return forwardRequest(request); };
+    }
