+
+    CDataStream hdrbuf; // partially received header
+    CMessageHeader hdr; // complete header
+    unsigned int nHdrPos;
+
+    unsigned int nDataPos;
+
+    NetMessage(const CMessageHeader::MessageStartChars& pchMessageStartIn, int nTypeIn, int nVersionIn) : NetMessageBase(pchMessageStartIn, nTypeIn, nVersionIn), hdrbuf(nTypeIn, nVersionIn), hdr(pchMessageStartIn)
+    {
+        hdrbuf.resize(24);
+        in_data = false;
+        nHdrPos = 0;
+        nDataPos = 0;
+    }
+
+    bool complete() const
+    {
+        if (!in_data)
+            return false;
+        return (hdr.nMessageSize == nDataPos);
+    }
+
+    uint32_t getMessageSize() const
+    {
+        if (!in_data) {
+            return 0;
+        }
+        return hdr.nMessageSize;
+    }
+
+    uint32_t getMessageSizeWithHeader() const
+    {
+        return hdr.nMessageSize + CMessageHeader::HEADER_SIZE;
+    }
+
+    std::string getCommandName() const
+    {
+        return hdr.pchCommand;
+    }
+
+    const uint256& GetMessageHash() const;
+
+    void SetVersion(int nVersionIn)
+    {
+        hdrbuf.SetVersion(nVersionIn);
+        NetMessageBase::SetVersion(nVersionIn);
+    }
+
+    int read(const char* pch, unsigned int nBytes);
+    int readHeader(const char* pch, unsigned int nBytes);
+    int readData(const char* pch, unsigned int nBytes);
+
+    bool verifyMessageStart() const;
+    bool verifyHeader() const;
+    bool verifyChecksum(std::string& error) const;

body:
Use `override` to ensure that these methods are overriding virtual methods from the base class.

```c++
    bool VerifyMessageStart() const override;
    bool VerifyHeader() const override;
    bool VerifyChecksum(std::string& error) const override;
```

