+bool CHub::EmitTransaction(CTransaction& tx)
+{
+    CTxDB txdb("r");
+    uint256 hash = tx.GetHash();
+
+    bool fMissingInputs = false;
+    if (mempool.accept(txdb, tx, true, &fMissingInputs))
+    {
