+ExternalSigner *GetSignerForJSONRPCRequest(const JSONRPCRequest& request, int index, CWallet* pwallet) {
+    if (pwallet->m_external_signers.empty()) {
+        throw JSONRPCError(RPC_WALLET_ERROR, "First call enumeratesigners");
