     virtual bool ToPrivateString(const SigningProvider& provider, std::string& out) const = 0;

+    /** What address type this descriptor produces when converted to a string. */
+    virtual Optional<AddressType> GetAddressType() const = 0;
+
     /** Expand a descriptor at a specified position.
      *
      * @param[in] pos: The position at which to expand the descriptor. If IsRange() is false, this is ignored.

@@ -29,6 +29,9 @@ constexpr int RANGE = 1; // Expected to be ranged descriptor
 constexpr int HARDENED = 2; // Derivation needs access to private keys
 constexpr int UNSOLVABLE = 4; // This descriptor is not expected to be solvable
 constexpr int SIGNABLE = 8; // We can sign with this descriptor (this is not true when actual BIP32 derivation is used, as that's not integrated in our signing code)
+constexpr int ADDRESS_TYPE_NONE = 16; // This descriptor should not have an AddressType
+constexpr int ADDRESS_TYPE_BASE58  = 32; // This descriptor should have AddressType::BASE58
+constexpr int ADDRESS_TYPE_BECH32  = 64; // This descriptor should have AddressType::BECH32

 /** Compare two descriptors. If only one of them has a checksum, the checksum is ignored. */
 bool EqualDescriptor(std::string a, std::string b)
@@ -105,6 +108,15 @@ void DoCheck(const std::string& prv, const std::string& pub, int flags, const st
     BOOST_CHECK(EqualDescriptor(prv, prv1));
     BOOST_CHECK(!parse_pub->ToPrivateString(keys_pub, prv1));

+    // Check if the address type is correct:
+    if (flags & ADDRESS_TYPE_NONE) {
+        BOOST_CHECK_EQUAL(parse_pub->GetAddressType(), nullopt);
+    } else if (flags & ADDRESS_TYPE_BASE58) {
+        BOOST_CHECK_EQUAL(parse_pub->GetAddressType(), AddressType::BASE58);
+    } else if (flags & ADDRESS_TYPE_BECH32) {
+        BOOST_CHECK_EQUAL(parse_pub->GetAddressType(), AddressType::BECH32);
+    }
+
     // Check whether IsRange on both returns the expected result
     BOOST_CHECK_EQUAL(parse_pub->IsRange(), (flags & RANGE) != 0);
     BOOST_CHECK_EQUAL(parse_priv->IsRange(), (flags & RANGE) != 0);
