+void TxPoolLayer::check() const
+{
+    std::list<const CTxMemPoolEntry*> waitingOnDependants;
+    std::vector<const CTxMemPoolEntry*> all_txs;
+    for (const CTxMemPoolEntry& e : m_cache_added) {
+        all_txs.emplace_back(&e);
+        assert(m_cache_map_links.find(txiter{txiter_nested{m_cache_added.iterator_to(e)}}) != m_cache_map_links.end());
+    }
+    for (const CTxMemPoolEntry& e : m_tx_pool.mapTx) {
+        if (m_cache_removed.find(e.GetTx().GetHash()) == m_cache_removed.end()) {
+            all_txs.emplace_back(&e);
+        }
+    }
+    for (const CTxMemPoolEntry* e : all_txs) {
+        const CTransaction& tx = e->GetTx();
+        const auto it = *GetIter(tx.GetHash());
+        setEntries setParentCheck;
+        for (const CTxIn& txin : tx.vin) {
+            if (exists(txin.prevout.hash)) {
+                setParentCheck.insert(*GetIter(txin.prevout.hash));
+            }
+            const CTransaction* c_tx = GetConflictTx(txin.prevout);
+            assert(c_tx = &tx);
+        }
+        for (const auto& p : GetMemPoolParents(it)) {
+            setParentCheck.erase(p);
+        }
+        assert(setParentCheck.size() == 0);
+
+        setEntries setChildrenCheck;
+        for (unsigned i{0}; i <= tx.vout.size(); ++i) {
+            if (const CTransaction* c_tx = GetConflictTx(COutPoint{tx.GetHash(), i})) {
+                setChildrenCheck.insert(*GetIter(c_tx->GetHash()));
+            }
+        }
+        for (const auto& c : GetMemPoolChildren(it)) {
+            setChildrenCheck.erase(c);
+        }
+        assert(setChildrenCheck.size() == 0);
+    }
+}
