+
+        }
+
+        VideoOutput {
+            id: viewfinderOutput
+
+            source: camera
+            autoOrientation: true
+            Layout.fillWidth: true
+            Layout.preferredHeight: width
+            Layout.alignment: Qt.AlignHCenter
+
+            fillMode: VideoOutput.PreserveAspectCrop
+
+            Camera {
+                id: camera
+                cameraState: Camera.UnloadedState
+            }
+
+            Rectangle {
+                id: captureZone
+                color: "red"
+                opacity: 0.2
+                width: parent.width * 0.8
