+    if ((flags & ArgsManager::ALLOW_BOOL) && value && *value == "0") return util::SettingsValue{false};
+    if ((flags & ArgsManager::ALLOW_BOOL) && (!value || *value == "1")) return util::SettingsValue{true};
+
