+static struct bufferevent* bevcb(struct event_base *base, void *arg) {
+    struct bufferevent* resp;
+    SSL_CTX *ctx = (SSL_CTX *) arg;
+    resp = bufferevent_openssl_socket_new(base,
+    -1,
+    SSL_new(ctx),
+    BUFFEREVENT_SSL_ACCEPTING,
+    BEV_OPT_CLOSE_ON_FREE);
+    return resp;
+}
