+static void
+blkcpy(uint32_t * dest, const uint32_t * src, size_t count)
+{
+	do {
+		*dest++ = *src++;
+	} while (--count);
+}
+
+static void
+blkxor(uint32_t * dest, const uint32_t * src, size_t count)
+{
+	do {
+		*dest++ ^= *src++;
+	} while (--count);
+}
