+int64 CWallet::GetAccountBalance(const string& strAccount, int nMinDepth)
+{
+	CWalletDB walletdb(strWalletFile);
+    int64 nBalance = 0;
+    // Moved out of loop to avoid some unneeded operations
+    int64 nReceived, nSent, nFee;
