+CNode* CConnman::FindNodeByIP(const CService& addr)
+{
+    LOCK(cs_vNodes);
+    for (CNode* pnode : vNodes) {
+        if ((static_cast<CService>(pnode->addr)).ToStringIP() == addr.ToStringIP()) {
+            return pnode;
+        }
+    }
+    return nullptr;
+}
