@@ -101,7 +101,7 @@ def parse_spec(s):
     host = name_to_bip155(host)
 
     if host[0] == BIP155Network.TORV2:
-        return -1  # TORV2 is no longer supported, so we ignore it
+        return None  # TORV2 is no longer supported, so we ignore it
     else:
         return host + (port, )
 
