     QLineEdit::clear();
 }
+
+void QValidatedLineEdit::setEnabled(bool enabled)
+{
+    if (!enabled)
+    {
+        // A disabled QValidatedLineEdit should be marked valid
+        setValid(true);
+    }
+    else
+    {
+        // Recheck validity when QValidatedLineEdit gets enabled
+        checkValidity();
+    }
+
+    QLineEdit::setEnabled(enabled);
+}
+
+void QValidatedLineEdit::checkValidity()
+{
+    if (text().isEmpty())
+    {
+        setValid(true);
+    }
+    else if (hasAcceptableInput())
+    {
+        setValid(true);
+
+        // Check BTC address on focus out
+        if (checkOnFocusOut)
+        {
+            if (BitcoinAddressValidator::validateAddress(text()))
+                setValid(true);
+            else
+                setValid(false);
+        }
+    }
+    else
+        setValid(false);
+}
+
+void QValidatedLineEdit::setBtcAddressField(bool state)
+{
+    checkOnFocusOut = state;
+}

