+bool CWallet::EraseAddressBookName(const std::string& strAddress)
+{
+    if (!fFileBacked)
+        return false;
+    if(CWalletDB(strWalletFile).EraseName(strAddress))
+    {
+        CRITICAL_BLOCK(cs_mapAddressBook)
