+
+void ProofOfWork::UpdateTime(const CBlockIndex* pindexPrev)
+{
+    nTime = std::max(pindexPrev->GetMedianTimePast()+1, GetAdjustedTime());
+
+    // Updating time can change work required on testnet:
+    if (Params().AllowMinDifficultyBlocks())
+        nBits = GetNextWorkRequired(pindexPrev, GetBlockTime());
+}
+
+ProofOfWork::ProofOfWork(unsigned int nTime, unsigned int nBits, unsigned int nNonce)
+{
+    this->nTime = nTime;
+    this->nBits = nBits;
+    this->nNonce = nNonce;
+}
