+double dHashesPerSec = 0.0;
+int64_t nHPSTimerStart = 0;
+//
+// ScanHash scans nonces looking for a hash with at least some zero bits.
+// The nonce is usually preserved between calls, but periodically or if the
+// nonce is 0xffff0000 or above, the block is rebuilt and nNonce starts over at
+// zero.
+//
+bool static ScanHash(const CBlockHeader *pblock, uint32_t& nNonce, uint256 *phash)
+{
+    // Write the first 76 bytes of the block header to a double-SHA256 state.
+    CHash256 hasher;
+    CDataStream ss(SER_NETWORK, PROTOCOL_VERSION);
+    ss << *pblock;
+    assert(ss.size() == 80);
+    hasher.Write((unsigned char*)&ss[0], 76);
+
+    while (true) {
+        nNonce++;
+
+        // Write the last 4 bytes of the block header (the nonce) to a copy of
+        // the double-SHA256 state, and compute the result.
+        CHash256(hasher).Write((unsigned char*)&nNonce, 4).Finalize((unsigned char*)phash);
+
+        // Return the nonce if the hash has at least some zero bits,
+        // caller will check if it has enough to reach the target
+        if (((uint16_t*)phash)[15] == 0)
+            return true;
+
+        // If nothing found after trying for a while, return -1
+        if ((nNonce & 0xffff) == 0)
+            return false;
+        if ((nNonce & 0xfff) == 0)
+            boost::this_thread::interruption_point();
+    }
+}
+
+bool ProofOfWork::GenerateProof(CBlockHeader* pblock, const CBlockIndex* pindexPrev)
+{
+    unsigned int nTransactionsUpdatedLast = mempool.GetTransactionsUpdated();
+    int64_t nStart = GetTime();
+    uint256 hashTarget = uint256().SetCompact(nBits);
+    uint256 hash;
+    uint32_t nNonce = 0;
+    uint32_t nOldNonce = 0;
+    while (true) {
+        bool fFound = ScanHash(pblock, nNonce, &hash);
+        uint32_t nHashesDone = nNonce - nOldNonce;
+        nOldNonce = nNonce;
+
+        // Check if something found
+        if (fFound)
+        {
+            if (hash <= hashTarget)
+            {
+                // Found a solution
+                this->nNonce = nNonce;
+                assert(hash == pblock->GetHash());
+
+                //// debug print
+                LogPrintf("BitcoinMiner:\n");
+                LogPrintf("proof-of-work found  \n  hash: %s  \ntarget: %s\n", hash.GetHex(), hashTarget.GetHex());
+
+                return true;
+            }
+        }
+
+        // Meter hashes/sec
+        static int64_t nHashCounter;
+        if (nHPSTimerStart == 0)
+        {
+            nHPSTimerStart = GetTimeMillis();
+            nHashCounter = 0;
+        }
+        else
+            nHashCounter += nHashesDone;
+        if (GetTimeMillis() - nHPSTimerStart > 4000)
+        {
+            static CCriticalSection cs;
+            {
+                LOCK(cs);
+                if (GetTimeMillis() - nHPSTimerStart > 4000)
+                {
+                    dHashesPerSec = 1000.0 * nHashCounter / (GetTimeMillis() - nHPSTimerStart);
+                    nHPSTimerStart = GetTimeMillis();
+                    nHashCounter = 0;
+                    static int64_t nLogTime;
+                    if (GetTime() - nLogTime > 30 * 60)
+                    {
+                        nLogTime = GetTime();
+                        LogPrintf("hashmeter %6.0f khash/s\n", dHashesPerSec/1000.0);
+                    }
+                }
+            }
+        }
+
+        // Check for stop or if block needs to be rebuilt
+        boost::this_thread::interruption_point();
+        // Regtest mode doesn't require peers
+        if (vNodes.empty() && Params().MiningRequiresPeers())
+            return false;
+        if (nNonce >= 0xffff0000)
+            return false;
+        if (mempool.GetTransactionsUpdated() != nTransactionsUpdatedLast && GetTime() - nStart > 60)
+            return false;
+        if (pindexPrev != chainActive.Tip())
+            return false;
+
+        // Update nTime every few seconds
+        UpdateTime(pindexPrev);
+        if (Params().AllowMinDifficultyBlocks())
+        {
+            // Changing pblock->nTime can change work required on testnet:
+            hashTarget.SetCompact(nBits);
+        }
+    }
+}
+
+bool ProofOfWork::CheckProof(const uint256 hash) const
+{
+    return CheckProofOfWork(hash, nBits);
+}
+
+bool ProofOfWork::CheckMinProof(const CBlockIndex* pcheckpoint) const
+{
+    int64_t deltaTime = GetBlockTime() - pcheckpoint->proof.GetBlockTime();
+    return CheckMinWork(nBits, pcheckpoint->proof.nBits, deltaTime);
+}
+
+bool ProofOfWork::CheckProofRequired(const CBlockIndex* pindexPrev) const
+{
+    return nBits == GetNextWorkRequired(pindexPrev, GetBlockTime());
+}
+
+void ProofOfWork::ResetProof(const CBlockIndex* pindexPrev)
+{
+    UpdateTime(pindexPrev);
+    nBits = GetNextWorkRequired(pindexPrev, GetBlockTime());
+    nNonce = 0;
+}
